	};
	return (
		<div className="App">
		  <header>
			<form id="to-do-form" onSubmit={this.addItem}>
			  <input type="text" placeholder="Enter task" value= {this.state.currentItem.text} onChange={this.handleInput}></input>
			  <button type="submit">Add</button>
			</form>
			<p>{this.state.items.text}</p>

